import {
  Card,
  Button,
  CardHeader,
  CardBody,
  CardTitle,
  CardText,
  Badge,
} from "reactstrap";
import "./MeetingCards.css";

export default ({ meeting }) => {
  const addFavorite = (card, user) => {
