  };

  return (
    <Card id="meeting-card-bg">
      <CardHeader id="meeting-name">
        {meeting.name}
        <Table.Cell textAlign="center">
          <Button
            onClick={() => addFavorite(card, props.loggedUser)}
            bordered
            color={favorite ? "google plus" : "twitter"}
            icon={favorite ? "heart" : "heart outline"}
          />
        </Table.Cell>
      </CardHeader>
      <CardBody>
        <CardTitle id="meeting-time">
          {meeting.day} at {meeting.time}
        </CardTitle>
        <CardText id="meeting-notes">{meeting.notes}</CardText>
        <Button id="meeting-link" href={meeting.link}>
          Zoom Link
        </Button>
      </CardBody>
    </Card>
  );
};
